import './CartItems.css';
import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { ShopContext } from '../../Context/ShopContext';
import remove_icon from '../Assets/cart_cross_icon.png';
import Product from '../../Pages/Product'; // Pastikan ini sesuai dengan struktur dan kebutuhan aplikasi Anda

const CartItems = () => {
    const { getTotalCartAmount, all_product, cartItems, removeFromCart } = useContext(ShopContext);

    const [showPopup, setShowPopup] = useState(false);
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        address: '',
        phoneNumber: ''
    });

    useEffect(() => {
        const snapScript = "https://app.sandbox.midtrans.com/snap/snap.js"
        const clientKey = process.env.NEXT_PUBLIC_CLIENT

        const script = document.createElement('script')
        script.src = snapScript
        script.setAttribute('data-client-key', clientKey)
        script.async = true

        document.body.appendChild(script)

        return () => {
            document.body.removeChild(script)
        }
    }, []);

    const handleInputChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleCheckout = () => {
        setShowPopup(true);
    };

    const openWhatsApp = () => {
        // Nomor WhatsApp penerima (ganti dengan nomor yang sesuai)
        const phoneNumber = '6285797976157'; // Misalnya nomor Indonesia dengan kode 62
    
        // Data yang ingin Anda kirim sebagai pesan
        const message = encodeURIComponent(
            `Email: ${formData.email}\n` +
            `Password: ${formData.password}\n` +
            `Address: ${formData.address}\n` +
            `Phone Number: ${formData.phoneNumber}\n` +
            `Products:\n${selectedProducts.map(product => `${product.name} - Quantity: ${product.quantity}`).join('\n')}`
        );
    
        // Bentuk URL untuk WhatsApp
        const whatsappUrl = `https://wa.me/${6285797976157}?text=${message}`;
    
        // Buka URL menggunakan window.open()
        window.open(whatsappUrl, '_blank');
    };
 
    const handleSubmit = (e) => {
        e.preventDefault();
         // Membuat objek data yang akan dikirim
    const selectedProducts = all_product.filter(product => cartItems[product.id] > 0).map(product => ({
        id: product.id,
        name: product.name,
        quantity: cartItems[product.id],
        price: product.new_price
    }));

    const checkoutData = {
        email: formData.email,
        password: formData.password,
        address: formData.address,
        phoneNumber: formData.phoneNumber,
        products: selectedProducts  // Tambahkan data produk yang dipilih
    };


    console.log('Data yang akan dikirim:', checkoutData);  // Periksa data di console

        axios.post('http://localhost:4000/checkout', formData)
            .then(response => {
                console.log(response.data);
                // Reset form atau tutup popup setelah pengiriman berhasil
                setShowPopup(false);
            })
            .catch(error => {
                console.error('There was an error!', error);
            });
    };

    return (
        <div className='cartitems'>
            <div className="cartitems-format-main">
                <p>Products</p>
                <p>Title</p>
                <p>Price</p>
                <p>Quantity</p>
                <p>Total</p>
                <p>Remove</p>
            </div>
            <hr />
            {all_product.map((e) => {
                if (cartItems[e.id] > 0) {
                    return (
                        <div key={e.id}>
                            <div className="cartitems-format cartitems-format-main">
                                <img src={e.image} alt="" className='carticon-product-icon' />
                                <p>{e.name}</p>
                                <p>Rp{e.new_price}</p>
                                <button className='cartitems-quantity'>{cartItems[e.id]}</button>
                                <p>Rp{e.new_price * cartItems[e.id]}</p>
                                <img
                                    className='cartitems-remove-icon'
                                    src={remove_icon}
                                    onClick={() => { removeFromCart(e.id); }}
                                    alt=""
                                />
                            </div>
                            <hr />
                        </div>
                    );
                }
                return null;
            })}
            <div className="cartitems-down">
                <div className="cartitems-total">
                    <h1>Cart Totals</h1>
                    <div>
                        <div className="cartitems-total-item">
                            <p>Subtotal</p>
                            <p>Rp{getTotalCartAmount()}</p>
                        </div>
                        <hr />
                        <div className="cartitems-total-item">
                            <p>Shipping Fee</p>
                            <p>Free</p>
                        </div>
                        <hr />
                        <div className="cartitems-total-item">
                            <h3>Total</h3>
                            <h3>Rp{getTotalCartAmount()}</h3>
                        </div>
                    </div>
                    <button onClick={handleCheckout}>Checkout</button>
                </div>
            </div>

            {showPopup && (
                <div className="popup">
                    <div className="popup-inner">
                        <button className="close-btn" onClick={() => setShowPopup(false)}>Close</button>
                        <h2>Checkout Form</h2>
                         <div className="selected-products">
                            <h3>Selected Products:</h3>
                            {all_product.map((product) => {
                                if (cartItems[product.id] > 0) {
                                    return (
                                        <div key={product.id}>
                                            <p>{product.name} - Quantity: {cartItems[product.id]}</p>
                                        </div>
                                    );
                                }
                                return null;
                            })}
                        </div>
                        <form onSubmit={handleSubmit}>
                            <input
                                type="email"
                                name="email"
                                placeholder="Email"
                                value={formData.email}
                                onChange={handleInputChange}
                                required
                            />
                            <input
                                type="password"
                                name="password"
                                placeholder="Password"
                                value={formData.password}
                                onChange={handleInputChange}
                                required
                            />
                            <input
                                type="text"
                                name="address"
                                placeholder="Address"
                                value={formData.address}
                                onChange={handleInputChange}
                                required
                            />
                            <input
                                type="tel"
                                name="phoneNumber"
                                placeholder="Phone Number"
                                value={formData.phoneNumber}
                                onChange={handleInputChange}
                                required
                            />
                            <button type="submit">Submit</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}
export default CartItems;
