import React, { useContext } from 'react';
import './CartItems.css';
import { ShopContext } from '../../Context/ShopContext';
import remove_icon from '../Assets/cart_cross_icon.png';
import Product from '../../Pages/Product';
import { useEffect } from 'react';
import axios from 'axios';


const CartItems = () => {
    const {getTotalCartAmount, getTotalCartItems, all_product, cartItems, removeFromCart } = useContext(ShopContext);
   
        useEffect(() => {
            const snapScript = "https://app.sandbox.midtrans.com/snap/snap.js"
            const clientKey = process.env.NEXT_PUBLIC_CLIENT

            const script = document.createElement('script')
            script.src = snapScript
            script.setAttribute('data-client-key', clientKey)
            script.async = true
    
            document.body.appendChild(script)
    
            return () => {
                document.body.removeChild(script)
            }
    
        }, []);
    
    

    // const checkout = async () => {
    //     const data = {
    //         id: Product.id,
    //         productName: Product.name,
    //         price: Product.new_price,
    //         quantity: cartItems

    //     }

        // const response = await fetch('/api/tokenizer', {
            // method: "POST",
            // body: JSON.stringify(data)
        // })

        const handlePayment = async () => {
            try {
              const response = await axios.post('http://localhost:4000/create-transaction', {
                amount: 10000, // nominal pembayaran
                customerDetails: {
                  first_name: "Nama Depan",
                  last_name: "Nama Belakang",
                  email: "email@example.com",
                  phone: "081234567890"
                }
              });
              const { token } = response.data;
              window.snap.pay(token);
            } catch (error) {
              console.error('Error:', error);
            }
          };
    
      
        

    //     const requestData = await response.json()
    //     window.snap.pay(requestData.token)
    //   };

    return (
        <div className='cartitems'>
            <div className="cartitems-format-main">
                <p>Products</p>
                <p>Title</p>
                <p>Price</p>
                <p>Quantity</p>
                <p>Total</p>
                <p>Remove</p>
            </div>
            <hr />
            {all_product.map((e)=>{
                if (cartItems[e.id]>0) 
                    {
                    return <div>
                            <div className="cartitems-format cartitems-format-main">
                                <img src={e.image} alt="" className='carticon-product-icon' />
                                <p>{e.name}</p>
                                <p>Rp{e.new_price}</p>
                                <button className='cartitems-quantity'>{cartItems[e.id]}</button>
                                <p>Rp{e.new_price*cartItems[e.id]}</p>
                                <img className='cartitems-remove-icon'
                                    src={remove_icon} 
                                    onClick={() => { removeFromCart(e.id); }} 
                                    alt=""/>
                            </div>
                            <hr />
                        </div>
                    
                }
                return null;
        })}
            <div className="cartitems-down">
                <div className="cartitems-total">
                    <h1>Cart Totals</h1>
                    <div>
                        <div className="cartitems-total-item">
                            <p>Subtotal</p>
                            <p>Rp{getTotalCartAmount()}</p>
                        </div>
                        <hr />
                        <div className="cartitems-total-item">
                            <p>Shipping Fee</p>
                            <p>Free</p>
                        </div>
                        <hr />
                        <div className="cartitems-total-item">
                            <h3>Total</h3>
                            <h3>Rp{getTotalCartAmount()}</h3>
                            </div>
                        </div>
                        <button onClick={handlePayment}>Checkout</button>

                    </div>
            </div>
       </div>
    )
}



export default CartItems;
