import './CartItems.css';
import React, { useContext, useState } from 'react';
import './CartItems.css';
import { ShopContext } from '../../Context/ShopContext';
import remove_icon from '../Assets/cart_cross_icon.png';
import Product from '../../Pages/Product'; // Pastikan ini sesuai dengan struktur dan kebutuhan aplikasi Anda
import { useEffect } from 'react';

const CartItems = () => {
const { getTotalCartAmount, all_product, cartItems, removeFromCart } = useContext(ShopContext);


const [shippingAddress, setShippingAddress] = useState('');
const [phoneNumber, setPhoneNumber] = useState('');

const [showPopup, setShowPopup] = useState(false);
const [formData, setFormData] = useState({
    email: '',
    password: '',
    address: '',
    phoneNumber: ''
});

useEffect(() => {
    const snapScript = "https://app.sandbox.midtrans.com/snap/snap.js"
    const clientKey = process.env.NEXT_PUBLIC_CLIENT

    const script = document.createElement('script')
    script.src = snapScript
    script.setAttribute('data-client-key', clientKey)
    script.async = true

    document.body.appendChild(script)

    return () => {
        document.body.removeChild(script)
    }

}, []);

const handleInputChange = (e) => {
    setFormData({
        ...formData,
        [e.target.name]: e.target.value
    });
};

const handleCheckout = () => {
    setShowPopup(true);
};

const handleSubmit = (e) => {
    e.preventDefault();
    // Kirim data formulir ke backend atau lakukan sesuai kebutuhan aplikasi Anda
    console.log(formData);
    // Reset formulir atau tutup pop-up setelah pengiriman berhasil
    setShowPopup(false);
};

return (
    <div className='cartitems'>
        <div className="cartitems-format-main">
            <p>Products</p>
            <p>Title</p>
            <p>Price</p>
            <p>Quantity</p>
            <p>Total</p>
            <p>Remove</p>
        </div>
        <hr />
        {all_product.map((e) => {
            if (cartItems[e.id] > 0) {
                return (
                    <div key={e.id}>
                        <div className="cartitems-format cartitems-format-main">
                            <img src={e.image} alt="" className='carticon-product-icon' />
                            <p>{e.name}</p>
                            <p>Rp{e.new_price}</p>
                            <button className='cartitems-quantity'>{cartItems[e.id]}</button>
                            <p>Rp{e.new_price * cartItems[e.id]}</p>
                            <img
                                className='cartitems-remove-icon'
                                src={remove_icon}
                                onClick={() => { removeFromCart(e.id); }}
                                alt=""
                            />
                        </div>
                        <hr />
                    </div>
                );
            }
            return null;
        })}
        <div className="cartitems-down">
            <div className="cartitems-total">
                <h1>Cart Totals</h1>
                <div>
                    <div className="cartitems-total-item">
                        <p>Subtotal</p>
                        <p>Rp{getTotalCartAmount()}</p>
                    </div>
                    <hr />
                    <div className="cartitems-total-item">
                        <p>Shipping Fee</p>
                        <p>Free</p>
                    </div>
                    <hr />
                    <div className="cartitems-total-item">
                        <h3>Total</h3>
                        <h3>Rp{getTotalCartAmount()}</h3>
                    </div>
                </div>
                <button onClick={handleCheckout}>Checkout</button>
            </div>
        </div>

        {/* Pop-up form */}
        {showPopup && (
            <div className="popup">
                <div className="popup-inner">
                    <button className="close-btn" onClick={() => setShowPopup(false)}>Close</button>
                    <h2>Checkout Form</h2>
                     {/* Menampilkan produk yang dipilih */}
                      <div className="selected-products">
                        <h3>Selected Products:</h3>
                        {all_product.map((product) => {
                            if (cartItems[product.id] > 0) {
                                return (
                                    <div key={product.id}>
                                        <p>{product.name} - Quantity: {cartItems[product.id]}</p>
                                    </div>
                                );
                            }
                            return null;
                        })}
                    </div>
                    <form onSubmit={handleSubmit}>
                        <input
                            type="email"
                            name="email"
                            placeholder="Email"
                            value={formData.email}
                            onChange={handleInputChange}
                            required
                        />
                        <input
                            type="password"
                            name="password"
                            placeholder="Password"
                            value={formData.password}
                            onChange={handleInputChange}
                            required
                        />
                        <input
                            type="text"
                            name="address"
                            placeholder="Address"
                            value={formData.address}
                            onChange={handleInputChange}
                            required
                        />
                        <input
                            type="tel"
                            name="phoneNumber"
                            placeholder="Phone Number"
                            value={formData.phoneNumber}
                            onChange={handleInputChange}
                            required
                        />
                        <button type="submit">Submit</button>
                    </form>
                </div>
            </div>
        )}
    </div>
);
}
export default CartItems;
