import React, { useContext, useState } from 'react';
import './ProductDisplay.css';
import star_icon from '../Assets/star_icon.png';
import star_dull_icon from '../Assets/star_dull_icon.png';
import { ShopContext } from '../../Context/ShopContext';

const ProductDisplay = (props) => {
    const { product } = props;
    const { addToCart } = useContext(ShopContext);
    const [selectedSize, setSelectedSize] = useState(''); // State untuk menyimpan ukuran yang dipilih

    // Fungsi untuk menangani pemilihan ukuran
    const handleSizeSelection = (size) => {
        setSelectedSize(size);
    };

    // Handle untuk menambahkan produk ke keranjang dengan ukuran yang dipilih
    const handleAddToCart = () => {
        if (selectedSize) {
            addToCart(product.id, selectedSize); // Panggil addToCart dengan ID produk dan ukuran yang dipilih
            setSelectedSize(''); // Reset selectedSize setelah ditambahkan ke keranjang
        } else {
            alert('Please select a size before adding to cart.'); // Beri peringatan jika ukuran belum dipilih
        }
    };

    return (
        <div className='productdisplay'>
            <div className='productdisplay-left'>
                <div className='productdisplay-img-list'>
                    <img src={product.image} alt='' />
                    <img src={product.image} alt='' />
                    <img src={product.image} alt='' />
                    <img src={product.image} alt='' />
                </div>
                <div className='productdisplay-img'>
                    <img className='productdisplay-main-img' src={product.image} alt='' />
                </div>
            </div>
            <div className='productdisplay-right'>
                <h1>{product.name}</h1>
                <div className='productdisplay-right-stars'>
                    <img src={star_icon} alt='' />
                    <img src={star_icon} alt='' />
                    <img src={star_icon} alt='' />
                    <img src={star_icon} alt='' />
                    <img src={star_dull_icon} alt='' />
                    <p>(122)</p>
                </div>
                <div className='productdisplay-right-prices'>
                    <div className='productdisplay-right-price-old'>Rp{product.old_price}</div>
                    <div className='productdisplay-right-price-new'>Rp{product.new_price}</div>
                </div>
                <div className='productdisplay-right-description'>
                    A lightweight, usually knitted, pullover shirt, close fitting and a a round neckline short
                </div>
                <div className='productdisplay-right-size'>
                    <h1>Select Size</h1>
                    <div className='productdisplay-right-sizes'>
                        {/* Menambahkan event onClick untuk memilih ukuran */}
                        <div className={selectedSize === 'S' ? 'selected' : ''} onClick={() => handleSizeSelection('S')}>
                            S
                        </div>
                        <div className={selectedSize === 'M' ? 'selected' : ''} onClick={() => handleSizeSelection('M')}>
                            M
                        </div>
                        <div className={selectedSize === 'L' ? 'selected' : ''} onClick={() => handleSizeSelection('L')}>
                            L
                        </div>
                        <div className={selectedSize === 'XL' ? 'selected' : ''} onClick={() => handleSizeSelection('XL')}>
                            XL
                        </div>
                        <div className={selectedSize === 'XXL' ? 'selected' : ''} onClick={() => handleSizeSelection('XXL')}>
                            XXL
                        </div>
                    </div>
                </div>
                {/* Tombol "ADD TO CART" dengan pemilihan ukuran */}
                <button onClick={() => addToCart(product.id, selectedSize)}>ADD TO CART</button>
                <p className='productdisplay-right-category'>
                    <span>Category : </span>{product.category}
                </p>
                <p className='productdisplay-right-category'>
                    <span>Tags : </span>{product.tags}
                </p>
            </div>
        </div>
    );
};

export default ProductDisplay;
